'use strict';

var config = require('./generate-room-data');

/**
 * setupNav
 * @param  {string} parentClass
 * @param  {object} data
 */

function setupNav(parentClass, data) {
    if (!parentClass || !data) {
        return;
    }

    var activeClass = '',
        outputListItem,
        outputOption;

    if (data.id === 'deluxe') {
        activeClass = ' class="active"';
    }

    outputListItem = '<li data-id="' + data.id +
        '"' + activeClass + '>' + data.name + '</li>';

    outputOption = '<option data-id="' + data.id + '" value="' +
        data.id + '">' + data.name + '</option>';

    $('ul' + parentClass).append(outputListItem);
    $('select' + parentClass).append(outputOption);
}

/**
 * setupFriendsMessage
 * @param  {object} data
 */

function setupFriendsMessage(data) {
    if (!data || !data.message) {
        return;
    }

    $('#' + data.id + ' .friends-info')
        .append(data.message)
        .toggleClass('hidden');
}

/**
 * init
 */

module.exports = function init() {
    var navClass = '.room-list' ,
        data = config(),
        l = data.length,
        i = 0;

    if (!$(navClass).length) {
        return;
    }

    for (i; i < l; i += 1) {
        setupNav(navClass, data[i]);
        setupFriendsMessage(data[i]);
    }
};
'use strict';

/**
 * handleDisplay
 * @param  {string} id
 */

function handleDisplay(id) {
    var $el = $('#' + id);

    $el.siblings('article').hide();
    $el.fadeIn('slow');
}

/**
 * handleSelect attach event on drop-down
 */

function handleSelect() {
    var $dropDown = $('select.room-list');

    $dropDown.on('change', function(e) {
        handleDisplay(e.target.value);
        $('ul.room-list li').removeClass('active');
        $('[data-id="' + e.target.value + '"]')
            .addClass('active');
    });
}

/**
 * handleList attach event on list items
 */

function handleList() {
    var $roomList = $('ul.room-list');

    $roomList.children('li').on('click', function(e) {
        handleDisplay($(e.target).attr('data-id'));
        $(this).siblings('li').removeClass('active');
        $(this).addClass('active');
    });
}

/**
 * setupEvenHandlers
 */

module.exports = function setupEventHandlers() {
    handleList();
    handleSelect();
};
'use strict';

var getRoomsData = require('./process-data'),
    generateMessage = require('./generate-friends-message'),
    generateId = require('./id-generator'),
    pretifyName = require('./pretify-name'),
    roomsData = getRoomsData();

/**
 * generateRoomData
 * @param  {string} roomName
 * @param  {array} friendsData
 * @return {object}
 */

function generateRoomData(roomName, friendsData) {
    if (!roomName || !friendsData ||
        typeof roomName !== 'string' ||
        !Array.isArray(friendsData)) {

        return;
    }

    var response = {
        name: pretifyName(roomName),
        message: generateMessage(friendsData),
        id: generateId(roomName)
    };

    return response;
}

/**
 * generateRoomsConfig
 * @return {array} array of room data objects
 */

module.exports = function generateRoomsConfig() {
    var room,
        name,
        friends,
        response = [];

    for (room in roomsData) {
        if (roomsData.hasOwnProperty(room)) {
            name = room;

            if (roomsData[room].hasOwnProperty('friends')) {
                friends = roomsData[room].friends;
                response.push(generateRoomData(name, friends));
            }
        }
    }

    return response;
};
'use strict';

var data = require('../friends.json');

/**
 * processData
 * @param  {JSON} dataFile as json file dependency
 * @return {JSON}
 */

module.exports = function processData(dataFile) {
    if (!dataFile) {
        return data;
    }
    return dataFile;
};
'use strict';

/**
 * generateMessage
 * @param  {array} friends
 * @return {string} compiled message for nr of friends
 */

module.exports = function generateMessage(friends) {
    if (!friends || !Array.isArray(friends) || !friends.length) {
        return;
    }

    var friendsCount = friends.length,
        messageSuffix = ' stayed here',
        messageOtherFriend = ' other friend',
        message,
        chosenOnes,
        remaining;

    if (friendsCount > 1) {
        friends.sort();
    }

    if (friendsCount === 1) {
        message = friends[0] + ' has' + messageSuffix;

        return message;
    }

    if (friendsCount === 2) {
        message = friends.join(' and ') + ' have' +
            messageSuffix;

        return message;
    }

    if (friendsCount > 2) {
        chosenOnes = friends.slice(0,2);
        remaining = friendsCount - 2;

        message = chosenOnes.join(', ') + ' and ' + remaining +
            messageOtherFriend +
            (remaining > 1 ? 's' : '')  +
            ' have' + messageSuffix;

        return message;
    }
};
'use strict';

/**
 * generateId generate hyphenated lowercase string
 * @param  {string} name
 * @return {string}
 */

module.exports = function generateId(name) {
    if (!name || name === '' || typeof name !== 'string') {
        return;
    }

    var fixedName = name.replace(/\s/, '-');

    return fixedName.toLowerCase();
};
'use strict';

/**
 * pretifyName replaces '-' with spaces and capitalize
 * @param  {string} name
 * @return {string}
 */

module.exports = function pretifyName(name) {
    if (!name || name === '' || typeof name !== 'string') {
        return;
    }

    var fixedName = name.replace(/(\-|\s)/, ' '),
        prettyName = fixedName.replace(/(?:^|\s)\S/g , function(part) {
            return part.toUpperCase();
        });

    return prettyName;
};
